mapper.select:
select *,datediff(wDate, now()) as date_diff,
  timestampdiff(hour, wDate, now()) as time_diff
  from board order by idx desc limit #{startIndexNo},#{pageSize};

select *,datediff(wDate, now()) as date_diff,
  timestampdiff(hour, wDate, now()) as time_diff 
	from board where ${search} like concat("%",#{searchString},"%") order by idx desc limit #{startIndexNo},#{pageSize};
